package argbeast;

import beast.evolution.alignment.Alignment;
import beast.evolution.alignment.Sequence;
import beast.evolution.tree.Node;
import beast.util.ClusterTree;
import java.io.ByteArrayInputStream;
import java.util.ArrayList;
import java.util.List;
import javax.xml.parsers.DocumentBuilderFactory;
import org.junit.Test;
import static org.junit.Assert.*;
import org.w3c.dom.Document;
import org.w3c.dom.NodeList;

/**
 * Tests the toString() and fromXML() methods of RecombinationGraph.
 *
 * @author Tim Vaughan <tgvaughan@gmail.com>
 */
public class ChainResumptionTest {
    
    public ChainResumptionTest() { }
    
    @Test
    public void test() throws Exception {
        List<Sequence> sequences = new ArrayList<Sequence>();
        sequences.add(new Sequence("Tarsius_syrichta","-TGCAAGAACTGCTAACTCATGCCTCCATATATAACAATGTGGCTTTCTT-ACTTTTAAAGGATAGAAGTAATCCATCGGTCTTAGGAACCGAAAA-ATTGGTGCAACTCCAAATAAAAGTAATAAATTTATTTTCATCCTCCATTTTACTATCACTTACACTCTTAATTACCCCATTTATTATTACAACAACTAAAAAATATGAAACACATGCATACCCTTACTACGTAAAAAACTCTATCGCCTGCGCATTTATAACAAGCCTAGTCCCAATGCTCATATTTCTATACACAAATCAAGAAATAATCATTTCCAACTGACATTGAATAACGATTCATACTATCAAATTATGCCTAAGCTT"));
        sequences.add(new Sequence("Lemur_catta","-AAAACACTAGATTGTGAATCCAGAAATAGAAGCTCAAAC-CTTCTTATTTACCGAGAAAGTAATGTATGAACTGCTAACTCTGCACTCCGTATATAAAAATACGGCTATCTCAACTTTTAAAGGATAGAAGTAATCCATTGGCCTTAGGAGCCAAAAA-ATTGGTGCAACTCCAAATAAAAGTAATAAATCTATTATCCTCTTTCACCCTTGTCACACTGATTATCCTAACTTTACCTATCATTATAAACGTTACAAACATATACAAAAACTACCCCTATGCACCATACGTAAAATCTTCTATTGCATGTGCCTTCATCACTAGCCTCATCCCAACTATATTATTTATCTCCTCAGGACAAGAAACAATCATTTCCAACTGACATTGAATAACAATCCAAACCCTAAAACTATCTATTAGCTT"));
        sequences.add(new Sequence("Homo_sapiens","-CGACCCCTTATTTACCGAGAAAGCT-"));
        sequences.add(new Sequence("Pan","-CGACCCCTTATTTACCGAGAAAGCT-"));
        sequences.add(new Sequence("Gorilla","-CAACCCCTTATTTACCGAGAAAGCT-"));
        sequences.add(new Sequence("Pongo","-CAACCCCTTATTTACCGAGAAAGCT-CACAAGAACTGCTAACTCTCACT-"));
        sequences.add(new Sequence("Hylobates","-AAACCTCTTGCTTACCGAGAAAGCC-"));
        sequences.add(new Sequence("Macaca_fuscata","-CCACCTCTTATTTACCGAGAAAACT-"));
        sequences.add(new Sequence("M_mulatta","-CCACCTCTTATTTACCGAGAAAACT-"));
        sequences.add(new Sequence("M_fascicularis","-CCACTTCTTATTTACCGAGAAAACT-"));
        sequences.add(new Sequence("M_sylvanus","-CCACTTCTTATTTACCGAGAAAACT-"));
        sequences.add(new Sequence("Saimiri_sciureus","-TAACTTCTTAATTACCGAGAAAGTG-CGCAAGAACTGCTAATTCATGCTCCCAAGACTAACAACTTGGCTTCCTCAACTTTTAAAGGATAGTAGTTATCCATTGGTCTTAGGAGCCAAAAACATTGGTGCAACTCCAAATAAAAGTAATA---ATACACTTCTCCATCACTCTAATAACACTAATTAGCCTACTAGCGCCAATCCTAGCTACCCTCATTAACCCTAACAAAAGCACACTATACCCGTACTACGTAAAACTAGCCATCATCTACGCCCTCATTACCAGTACCTTATCTATAATATTCTTTATCCTTACAGGCCAAGAATCAATAATTTCAAACTGACACTGAATAACTATCCAAACCATCAAACTATCCCTAAGCTT"));
        Alignment alignment = new Alignment(sequences, 4, "nucleotide");
        
        // RecombinationGraph
        RecombinationGraph arg = new RecombinationGraph();
        ClusterTree tree = new ClusterTree();
        tree.initByName(
                "clusterType", "upgma",
                "taxa", alignment);
        
        arg.assignFrom(tree);
        arg.initByName("alignment", alignment);
        
        //Add recombination event 1
        Node node1 = arg.getExternalNodes().get(0);
        Node node2 = node1.getParent();
        double height1 = 0.5*(node1.getHeight() + node1.getParent().getHeight());
        double height2 = 0.5*(node2.getHeight() + node2.getParent().getHeight());
        int startLocus = 100;
        int endLocus = 200;
        Recombination recomb1 = new Recombination(node1, height1, node2, height2,
                startLocus, endLocus);
        arg.addRecombination(recomb1);
        
        //Add recombination event 2
        node1 = arg.getExternalNodes().get(0);
        node2 = arg.getRoot();
        height1 = 0.5*(node1.getHeight() + node1.getParent().getHeight());
        height2 = node2.getHeight() + 1.0;
        startLocus = 300;
        endLocus = 400;
        Recombination recomb2 = new Recombination(node1, height1, node2, height2,
                startLocus, endLocus);
        arg.addRecombination(recomb2);
        
        // Write ARG out to XML string
        String xmlStr = arg.toXML();
        
        // Build DOM from XML string
        DocumentBuilderFactory factory = DocumentBuilderFactory.newInstance();
        Document doc = factory.newDocumentBuilder().parse(new ByteArrayInputStream(xmlStr.getBytes()));
        doc.normalize();
        NodeList nodes = doc.getElementsByTagName("*");
        org.w3c.dom.Node docNode = nodes.item(0);
        
        // Read ARG back in from DOM
        RecombinationGraph argNew = new RecombinationGraph();
        argNew.assignFrom(tree);
        argNew.initByName("alignment", alignment);
        argNew.fromXML(docNode);
        
        // Check that new ARG matches old
        Recombination newRecomb1 = argNew.getRecombinations().get(1);
        assertEquals(newRecomb1.getNode1().getNr(),recomb1.getNode1().getNr());
        assertEquals(newRecomb1.getNode2().getNr(),recomb1.getNode2().getNr());
        assertEquals(newRecomb1.getHeight1(),recomb1.getHeight1(), 1e-15);
        assertEquals(newRecomb1.getHeight2(),recomb1.getHeight2(), 1e-15);
        assertEquals(newRecomb1.getStartLocus(), recomb1.getStartLocus());
        assertEquals(newRecomb1.getEndLocus(), recomb1.getEndLocus());
        
        Recombination newRecomb2 = argNew.getRecombinations().get(2);
        assertEquals(newRecomb2.getNode1().getNr(),recomb2.getNode1().getNr());
        assertEquals(newRecomb2.getNode2().getNr(),recomb2.getNode2().getNr());
        assertEquals(newRecomb2.getHeight1(),recomb2.getHeight1(), 1e-15);
        assertEquals(newRecomb2.getHeight2(),recomb2.getHeight2(), 1e-15);
        assertEquals(newRecomb2.getStartLocus(), recomb2.getStartLocus());
        assertEquals(newRecomb2.getEndLocus(), recomb2.getEndLocus());
        
        // Note that there are minor differences in the tree due to
        // rounding errors.  Is this normal!?
    }
}